<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Login extends CI_Controller {	/**	 * Index Page for this controller.	 *	 */	public function __construct() {        parent::__construct();		$this->load->library('form_validation');		$this->load->model('adminmodel');	}		function index() {		$data['title'] = "Griya Rempah";		$this->load->view('login', $data);	}		function auth () {		$auth = $this->adminmodel->auth();		if ($auth >= 1) {			$row = $this->adminmodel->get_user_by_uname($this->input->post('username'));			$sesi = array(						'username' => $this->input->post('username'),						'realname' => $row['realname'],						'user_id' => $row['user_id']						);			$this->session->set_userdata($sesi);			redirect('dashboard');		} else {			$this->session->set_flashdata('error','<p>Username atau password salah !</p>');			redirect('login');		}	}		function logout()	{		$this->session->sess_destroy();			$this->session->set_flashdata('error','<p>Anda sudah logout</p>');		redirect('login');	}}/* End of file home.php *//* Location: ./application/controllers/welcome.php */